import { Injectable } from '@angular/core';
import * as signalR from '@microsoft/signalr';
import { UserContextService } from './user-context.service';
import { ToastrService } from 'ngx-toastr';
@Injectable({
  providedIn: 'root'
})
export class SignalrService {
  private userID: any;
  private email: any;
  private hubConnection: signalR.HubConnection;
  constructor(
    private toastrService: ToastrService,
    private userContextService: UserContextService,
  ) {
    this.userID = this.userContextService.user$._value.id;
    this.email = this.userContextService.user$._value.email
    ;
    this.hubConnection = new signalR.HubConnectionBuilder()
      .withUrl('http://localhost:5261/mailHub', {
        skipNegotiation: true,
        transport: signalR.HttpTransportType.WebSockets
      }).build();
   }
   async ngOnInit(): Promise<void>
    {
    // Call the API service function when the app component initializes
      try
       {
        await this.startSignalRConnection();
       }
      catch (error)
       {
        console.error('Error starting SignalR connection:', error)
       }
        // Handle connection startup errors here
  }
  async startSignalRConnection(): Promise<void> {

    if (this.hubConnection.state === 'Disconnected') {
      await this.hubConnection
        .start()
        .then(() => {
          console.log('SignalR connection started successfully.');
          // Implement any logic you need after a successful connection
        })
        .catch((error) => {
          console.error('Error starting SignalR connection:', error);
          throw error; // Propagate the error
        });
    } else {
      console.warn('SignalR connection is already in a connected or connecting state.');
    }
  }

   openNewPage(): void {
    const brwserInfo = navigator.userAgent;
    // console.log('User-Agent:', userAgent);
    if (this.hubConnection.state === 'Connected') {
      this.hubConnection.invoke('OpenNewPage', this.userID,this.email ,brwserInfo.toString()).catch((error) => {
        console.error('Error JoinPrivateChat:', error);
      });
    } else {
      console.error('SignalR connection is not in the "Connected" state.');
    }
  }
  leavePage(): void {
    const brwserInfo = navigator.userAgent;
    // console.log('User-Agent:', userAgent);
    this.hubConnection.invoke('LeavePage',this.userID ,brwserInfo.toString());
  }
  logOut(){
    const brwserInfo = navigator.userAgent;
    // console.log('User-Agent:', userAgent);
    if (this.hubConnection.state === 'Connected') {
      this.hubConnection.invoke('LeaveApplication', this.userID.toString(),"1", brwserInfo.toString()).catch((error) => {
        console.error('Error JoinPrivateChat:', error);
      });
    } else {
      console.error('SignalR connection is not in the "Connected" state.');
    }
  }
}
