import { Component } from '@angular/core';
import { CommonService } from 'src/app/common/common.service';
import { ConstantService } from 'src/app/common/constant.service';

@Component({
  selector: 'app-user-management',
  templateUrl: './user-management.component.html',
  styleUrls: ['./user-management.component.scss']
})
export class UserManagementComponent {
  User:any={};
  roles: string[] = ['Admin', 'User'];
  UserList:any=[];
  constructor(private commonService: CommonService,
    private constantsService: ConstantService,){

  }
  ngOnInit():void{

    this.User.id= 0;
    this.User.email= '';
    this.User.UserNamee= '';
    this.User.phoneNumber= '';
    this.User.location= '';
    this.User.password= '';
    this.User.role= '';
    this.User.isActive= true;
  // "id": 0,
  // "email": "string",
  // "phoneNumber": "string",
  // "location": "string",
  // "password": "string",
  // "userNamee": "string",
  // "role": "string"
  }
  GetUserList(){

    let url= this.constantsService.UserList
    console.log(this.User)
    this.commonService.get(url).subscribe(res=>{
      console.log(res);
  });
  }
  AddUpdate(){
    if(this.User ==0)
    {
        let url= this.constantsService.CreateUser
        console.log(this.User)
        this.commonService.post(url,this.User).subscribe(res=>{
          console.log(res);
      });
    }
    else{
      let url= this.constantsService.UpdateUser
      console.log(this.User)
      this.commonService.post(url,this.User).subscribe(res=>{
        console.log(res);
    });
    }
  }
}
<div class="container">
  <div class="row justify-content-center mt-4">
    <div class="col-6">
      <button type="button" class="btn btn_submit button-text" data-toggle="modal" data-target="#AddUserModal">Add User</button>
    </div>
  </div>
</div>

<!-- Modal -->
<div class="modal fade" id="AddUserModal" tabindex="-1" aria-labelledby="exampleModalLabel" aria-hidden="true">
  <div class="modal-dialog modal-lg">
    <div class="modal-content">
      <div class="modal-header">
        <h5 class="modal-title" id="exampleModalLabel">Add/Update User</h5>
        <button type="button" class="close" data-dismiss="modal" aria-label="Close">
          <span aria-hidden="true">&times;</span>
        </button>
      </div>

      <div class="modal-body">
        <form action="mailto:recipient@example.com" method="post" enctype="text/plain">
          <div class="form-group">
            <input type="hidden" [(ngModel)]="User.Toemail">
            <label for="subject">Email:</label>
            <input type="text" id="subject" name="subject" class="form-control" placeholder="Email" [(ngModel)]="User.email" (keyup.enter)="AddUpdate()">
          </div>
          <div class="form-group">
            <label for="subject">UserName:</label>
            <input type="text" id="subject" name="subject" class="form-control" placeholder="User Name" [(ngModel)]="User.UserNamee" (keyup.enter)="AddUpdate()">
          </div>
          <div class="form-group">
            <label for="subject">Phone Number:</label>
            <input type="text" id="subject" name="subject" class="form-control" placeholder="Phone Number" [(ngModel)]="User.phoneNumber" (keyup.enter)="AddUpdate()">
          </div>
          <div class="form-group">
            <label for="body">Location:</label>
            <input type="text" id="subject" name="subject" class="form-control" placeholder="Location" [(ngModel)]="User.location" (keyup.enter)="AddUpdate()">
          </div>
          <div class="form-group">
            <label for="body">Password:</label>
            <input type="text" id="subject" name="subject" class="form-control" placeholder="Password" [(ngModel)]="User.password" (keyup.enter)="AddUpdate()">
          </div>
          <div class="form-group">
            <label for="body">Role:</label>
            <select class="form-control" [(ngModel)]="User.role" name="role">
                <option *ngFor="let i of roles" [value]="i">{{i}}</option>
              </select>
          </div>
          <div class="form-check">
            <input type="checkbox" class="form-check-input" id="isActive" [(ngModel)]="User.isActive" name="isActive">
            <label class="form-check-label" for="isActive">Is Active</label>
          </div>
          <div class="text-center">
            <button type="submit" class="btn btn_submit button-text" (click)="AddUpdate()" data-dismiss="modal">Save</button>
          </div>
        </form>
      </div>
    </div>
  </div>
</div>

<div class="container mt-4">
  <div class="row justify-content-center">
    <h1>Users</h1>
  </div>
</div>
