      <div class="content_area mt-3 mb-4">
        <div class="form-group">
          <label class="label_style">Email</label>
          <input type="text" class="form-control mt-2" placeholder="Email" [(ngModel)]="usersingUp.email" (keyup.enter)="SignUp()"/>
