          <label class="label_style">Password</label>
          <input type="password" class="form-control mt-2" placeholder="Password" [(ngModel)]="usersingUp.password" (keyup.enter)="SignUp()"/>
        </div>
        <div class="form-group mt-3">
            <label class="label_style">phoneNumber</label>
            <input type="number" class="form-control mt-2" placeholder="phoneNumber" [(ngModel)]="usersingUp.phoneNumber" (keyup.enter)="SignUp()"/>
          </div>
          <div class="form-group mt-3">
            <label class="label_style">location</label>
            <input type="text" class="form-control mt-2" placeholder="location" [(ngModel)]="usersingUp.location" (keyup.enter)="SignUp()"/>
          </div>
