import { Component } from '@angular/core';
import { SignalrService } from 'src/app/common/signalr.service';

@Component({
  selector: 'app-inbox',
  templateUrl: './inbox.component.html',
  styleUrls: ['./inbox.component.scss']
})
export class InboxComponent {

constructor(public signalRService:SignalrService){

}
ngOnInit():void{
  //this.showAllChat()
 this.signalRService.getInbox();

}
}
<table>
    <thead>
      <tr>
        <th>Photo</th>
        <th>Box ID</th>
        <th>User Name</th>
        <th>Sender ID</th>
        <th>Last Email Title</th>
      </tr>
    </thead>
    <tbody>
      <tr *ngFor="let item of items">
        <td><img [src]="item.photoPath" alt="User Photo"></td>
        <td>{{ item.boxId }}</td>
        <td>{{ item.userName }}</td>
        <td>{{ item.senderId }}</td>
        <td>{{ item.lastEmailTitle || 'N/A' }}</td>
      </tr>
    </tbody>
  </table>

