import { Component } from '@angular/core';
import { CommonService } from 'src/app/common/common.service';
import { ConstantService } from 'src/app/common/constant.service';

@Component({
  selector: 'app-user-management',
  templateUrl: './user-management.component.html',
  styleUrls: ['./user-management.component.scss']
})
export class UserManagementComponent {
  User:any={};
  roles: string[] = ['Admin', 'User'];
  userList:any=[];
  constructor(private commonService: CommonService,
    private constantsService: ConstantService,){

  }
  ngOnInit():void{

    this.User.id= 0;
    this.User.email= '';
    this.User.UserNamee= '';
    this.User.phoneNumber= '';
    this.User.location= '';
    this.User.password= '';
    this.User.role= '';
    this.User.isActive= true;
    this.GetUserList()
  // "id": 0,
  // "email": "string",
  // "phoneNumber": "string",
  // "location": "string",
  // "password": "string",
  // "userNamee": "string",
  // "role": "string"
  }
  GetUserList(){

    let url= this.constantsService.UserList
    console.log(this.User)
    this.commonService.get(url).subscribe(res=>{
      this.userList = res.userLIst
      console.log(res);
  });
  }
  DeleteUser(id:any){
    let url= this.constantsService.DeleteUser
    console.log(this.User)
    this.commonService.get(url+"/"+id).subscribe(res=>{
      console.log(res);
  });
  }
  EditUser(item:any){
    debugger;
    this.User.id= item.id;
    this.User.email= item.userNamee;
    this.User.UserNamee= item.email;
    this.User.phoneNumber=item.phoneNumber;
    this.User.location=item.location;
    this.User.password= item.password;
    this.User.role= item.role;
    this.User.isActive= true;
  }
  AddUpdate(){
    if(this.User.id ==0)
    {
        let url= this.constantsService.CreateUser
        console.log(this.User)
        this.commonService.post(url,this.User).subscribe(res=>{
          console.log(res);
      });
      this.GetUserList()
    }
    else{
      let url= this.constantsService.UpdateUser
      console.log(this.User)
      this.commonService.post(url,this.User).subscribe(res=>{
        console.log(res);
    });
    this.GetUserList()
    }
  }
}
<div class="container">
  <div class="row justify-content-center mt-4">
    <div class="col-6">
      <button type="button" class="btn btn_submit button-text" data-toggle="modal" data-target="#AddUserModal">Add User</button>
    </div>
  </div>
</div>
<!-- Modal -->
<div class="modal fade" id="AddUserModal" tabindex="-1" aria-labelledby="exampleModalLabel" aria-hidden="true">
  <div class="modal-dialog modal-lg">
    <div class="modal-content">
      <div class="modal-header">
        <h5 class="modal-title" id="exampleModalLabel">Add/Update User</h5>
        <button type="button" class="close" data-dismiss="modal" aria-label="Close">
          <span aria-hidden="true">&times;</span>
        </button>
      </div>

      <div class="modal-body">
        <form action="mailto:recipient@example.com" method="post" enctype="text/plain">
          <div class="form-group">
            <input type="hidden" name="id" [ngModelOptions]="{ standalone: true }"  [(ngModel)]="User.id">
            <label for="subject">Email:</label>
            <input type="text" id="email" name="email" class="form-control" placeholder="Email" [ngModelOptions]="{ standalone: true }"  [(ngModel)]="User.email" (keyup.enter)="AddUpdate()">
          </div>
          <div class="form-group">
            <label for="subject">UserName:</label>
            <input type="text" id="UserNamee" name="UserNamee" class="form-control" placeholder="User Name"[ngModelOptions]="{ standalone: true }"  [(ngModel)]="User.UserNamee" (keyup.enter)="AddUpdate()">
          </div>
          <div class="form-group">
            <label for="subject">Phone Number:</label>
            <input type="text" id="phoneNumber" name="phoneNumber" class="form-control" placeholder="Phone Number" [ngModelOptions]="{ standalone: true }"  [(ngModel)]="User.phoneNumber" (keyup.enter)="AddUpdate()">
          </div>
          <div class="form-group">
            <label for="body">Location:</label>
            <input type="text" id="location" name="location" class="form-control" placeholder="Location" [ngModelOptions]="{ standalone: true }"  [(ngModel)]="User.location" (keyup.enter)="AddUpdate()">
          </div>
          <div class="form-group">
            <label for="body">Password:</label>
            <input type="text" id="password" name="password" class="form-control" placeholder="Password" [ngModelOptions]="{ standalone: true }"  [(ngModel)]="User.password" (keyup.enter)="AddUpdate()">
          </div>
          <div class="form-group">
            <label for="body">Role:</label>
            <select class="form-control"[ngModelOptions]="{ standalone: true }"  [(ngModel)]="User.role" name="role">
                <option *ngFor="let i of roles" [value]="i">{{i}}</option>
              </select>
          </div>
          <div class="form-check">
            <input type="checkbox" class="form-check-input" id="isActive"[ngModelOptions]="{ standalone: true }"  [(ngModel)]="User.isActive" name="isActive">
            <label class="form-check-label" for="isActive">Is Active</label>
          </div>
          <div class="text-center">
            <button type="submit" class="btn btn_submit button-text" (click)="AddUpdate()" data-dismiss="modal">Save</button>
          </div>
        </form>
      </div>
    </div>
  </div>
</div>

<div class="container mt-4">
  <div class="row justify-content-center">
    <h1>Users</h1>
  </div>
  <table class="table table-bordered table-striped table-hover">
    <thead class="thead-dark">
      <tr>
        <th></th>
        <th>id</th>
        <th>userName</th>
        <th>email</th>
        <th>location</th>
        <th>phoneNumber</th>
        <th>role</th>
        <th>isActive</th>
        <th>Action</th>
      </tr>
    </thead>
    <tbody>
      <tr *ngFor="let item of userList">
        <td>
          <img [src]="item.photoPath || 'data:image/png;base64,'"
               alt="User Photo" class="img-thumbnail rounded-circle" style="max-width: 50px; max-height: 50px;">
        </td>
        <td>{{ item.id }}</td>
        <td>{{ item.userNamee }}</td>
        <td>{{ item.email }}</td>
        <td>{{ item.location || 'N/A' }}</td>
        <td>{{ item.phoneNumber || 'N/A' }}</td>
        <td>{{ item.role || 'N/A' }}</td>
        <td>{{ item.isActive || 'N/A' }}</td>
        <td><button class="btn btn-info"(click)="EditUser(item)" data-toggle="modal" data-target="#AddUserModal"  >Edit</button> |<button class="btn btn-danger">Delete</button></td>
      </tr>
    </tbody>
  </table>
</div>
