import { Component, HostListener } from '@angular/core';
import * as signalR from '@microsoft/signalr';
import { SignalrService } from 'src/app/common/signalr.service';
import { UserContextService } from 'src/app/common/user-context.service';
@Component({
  selector: 'app-home',
  templateUrl: './home.component.html',
  styleUrls: ['./home.component.scss']
})
export class HomeComponent {
  message:any ={};
  userAcesse:boolean = false;
  private hubConnection: signalR.HubConnection;
  constructor(private signalRService: SignalrService,private userContextService: UserContextService){

     //SignalR
     this.hubConnection = new signalR.HubConnectionBuilder()
     .withUrl('http://localhost:5261/mailHub', {
       skipNegotiation: true,
       transport: signalR.HttpTransportType.WebSockets
     })
     .build();
  }
  async ngOnInit(): Promise<void> {
    this.userAcesse = this.userContextService.user$._value.userRole =="Admin"?true:false;
    this.message.Toemail = '';
    this.message.Subject = '';
    this.message.mailbody = '';
    // Call the API service function when the app component initializes
    try {
      await this.startSignalRConnection();
      this.signalRService.openNewPage();
      console.log("openNewPage is called");
    } catch (error) {
      console.error('Error starting SignalR connection:', error);
      // Handle connection startup errors here
    }
  }
  async startSignalRConnection(): Promise<void> {
    if (this.hubConnection.state === 'Disconnected') {
      await this.hubConnection
        .start()
        .then(() => {
          console.log('SignalR connection started successfully.');
          // Implement any logic you need after a successful connection
        })
        .catch((error) => {
          console.error('Error starting SignalR connection:', error);
          throw error; // Propagate the error
        });
    } else {
      console.warn('SignalR connection is already in a connected or connecting state.');
    }
  }
  @HostListener('window:beforeunload', ['$event'])
  unloadNotification($event: any): void {
    this.signalRService.leavePage();
    console.log("page is closed");
  }
  Send(){
    this.signalRService.sendPrivateMail(this.message.Toemail, this.message.Subject,this.message.mailbody , "New","0")
  }
}
<div class="container">
  <div class="w-100 d-flex">
    <!-- Left-side panel -->
    <div class="left-side flex-column p-3">
      <button type="button" class="btn btn_submit button-text" data-toggle="modal" data-target="#EmailCompose">Compose</button>
      <div class="d-flex w-100 justify-content-between p-2">
        <ul class="nav nav-tabs d-flex flex-column w-100 mt-4 border-0">
          <li class="nav-item">
            <a data-toggle="tab" class="nav-link w-100 d-flex justify-content-between active" href="#inbox">
              <div class="text-dark">
                <span><i class="far fa-envelope pr-3"></i></span>
                <span>Inbox</span>
              </div>
              <div class="count">3</div>
            </a>
          </li>
          <li class="nav-item mt-2">
            <a data-toggle="tab" href="#sent">
              <div class="text-dark">
                <span><i class="far fa-envelope pr-3"></i></span>
                <span>Sent</span>
              </div>
            </a>
          </li>
          <li *ngIf="" class="nav-item mt-2">
            <a [routerLink]="['/app-user-management']">Go to User Management</a>
          </li>
        </ul>
      </div>
    </div>

    <!-- Right-side content area -->
    <div class="right-side">
      <div class="input-group">
        <div class="input-group-prepend">
          <span class="input-group-text"><i class="fas fa-search"></i></span>
        </div>
        <input type="text" class="form-control" placeholder="Search email">
      </div>
      <hr class="p-0 m-0">
      <div class="tab-content">
        <div id="inbox" class="tab-pane fade in active">
          <app-inbox></app-inbox>
        </div>
        <div id="sent" class="tab-pane fade">
          <app-sentbox></app-sentbox>
        </div>
      </div>
    </div>
  </div>
</div>

<!-- Email Compose Modal -->
<div class="modal fade" id="EmailCompose" tabindex="-1" aria-labelledby="exampleModalLabel" aria-hidden="true">
  <div class="modal-dialog">
    <div class="modal-content">
      <div class="modal-header">
        <h5 class="modal-title" id="exampleModalLabel">New Mail</h5>
        <button type="button" class="close" data-dismiss="modal" aria-label="Close">
          <span aria-hidden="true">&times;</span>
        </button>
      </div>
      <div class="modal-body">
        <form action="mailto:recipient@example.com" method="post" enctype="text/plain">
          <label for="subject">To:</label><br>
          <input type="text" id="subject" name="subject" size="50" placeholder="To: ayz@gmail.com" [(ngModel)]="message.Toemail" (keyup.enter)="Send()">
          <hr>
          <label for="subject">Subject:</label><br>
          <input type="text" id="subject" name="subject" size="50" placeholder="title.." [(ngModel)]="message.Subject" (keyup.enter)="Send()">
          <hr>
          <label for="body">Message:</label><br>
          <textarea id="body" name="body" rows="10" cols="50" placeholder="abc......"  [(ngModel)]="message.mailbody" (keyup.enter)="Send()"></textarea>
          <hr>
          <input type="submit" class="btn btn_submit button-text" value="Send" (click)="Send()">
        </form>
      </div>
    </div>
  </div>
</div>
